import logo from './logo.svg';
import './App.css';
import Row from 'react-bootstrap/Row'
import HeaderComponent from './Components/HeaderComponent';
import UserCards from './Components/UserCards'
import { useEffect, useState } from 'react';
import CardList from './Components/CardList';
import Pagination from 'react-bootstrap/Pagination';


function App() {
  const [apiResponse, setapiResponse] = useState([])
  const [searchField, setsearchField] = useState("")
  const [pagination, setPagination] = useState({
    data: new Array(1000).fill().map((value, index) => (({
      id: index,
      title: faker.lorem.words(5),
      body: faker.lorem.sentences(8)
    }))),
    offset: 0,
    numberPerPage: 10,
    pageCount: 0,
    currentData: []
  });
  useEffect(() => {
    setPagination((prevState) => ({
      ...prevState,
      pageCount: prevState.data.length / prevState.numberPerPage,
      currentData: prevState.data.slice(pagination.offset, pagination.offset + pagination.numberPerPage)
    }))
  }, [pagination.numberPerPage, pagination.offset])
  const handlePageClick = event => {
    const selected = event.selected;
    const offset = selected * pagination.numberPerPage
    setPagination({ ...pagination, offset })
  }
  useEffect(() => {
    fetch('https://api.enye.tech/v1/challenge/records')
      .then(response=> response.json())
      .then(users => setapiResponse(users.records.profiles));
  }, [])

  const filteredRecords = apiResponse.filter(records =>{
    return records.FirstName.toLowerCase().includes(searchField.toLowerCase());
  })

  return (
    <div style={{maxWidth:"100%"}}>
          <HeaderComponent inputChange={(searchField)=> setsearchField(searchField)}/>
          <CardList records= {filteredRecords}/>
    </div>
  );
}

export default App;
