import logo from './logo.svg';
import './App.css';
import Row from 'react-bootstrap/Row'
import HeaderComponent from './Components/HeaderComponent';
import UserCards from './Components/UserCards'
import { useEffect, useState } from 'react';
import CardList from './Components/CardList';
import ReactPaginate from 'react-paginate';
import Pagination from '@material-ui/lab/Pagination';
import DropdownButton from 'react-bootstrap/DropdownButton'

function App() {
  const [apiResponse, setapiResponse] = useState([]);
  const [searchField, setsearchField] = useState("");
  const [offset, setoffset] = useState(0);
  const perPage = 20
  const [currentPage, setcurrentPage] = useState(0)
  const [sliceVal, setsliceVal] = useState([])
  const [pageCount, setpageCount] = useState(1)

  useEffect(() => {
    fetch('https://api.enye.tech/v1/challenge/records')
      .then(response=> response.json())
      .then(users => {setapiResponse(users.records.profiles)});
  }, [])

  const filteredRecords = apiResponse.filter(records =>{
    return records.FirstName.toLowerCase().includes(searchField.toLowerCase());
  })

  useEffect(() =>{
    const data = filteredRecords;
    const slice = data.slice(offset, offset + perPage)
    setsliceVal(slice) 
    setpageCount(Math.ceil(data.length / perPage))
  },[apiResponse])

  useEffect(() =>{
    const data = filteredRecords;
    setoffset(0)
    const slice = data.slice(offset, offset + perPage)
    setsliceVal(slice) 
    setpageCount(Math.ceil(data.length / perPage))
  },[searchField])

  useEffect(() => {
    const data = filteredRecords;
      const slice = data.slice(offset, offset + perPage)
      setsliceVal(slice)
      setpageCount(Math.ceil(data.length / perPage))
  },[offset,perPage])

  function handlePageClick (event, value) {
    const selectedPage = value;
    const offset = selectedPage * perPage;
    setcurrentPage(selectedPage);
    setoffset(offset);
    }

  return (
    <div style={{width:"100%",maxWidth:"100%"}}>
          <HeaderComponent 
            inputChange={(searchField)=> setsearchField(searchField)}/>
            <Dropdown>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Dropdown Button
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
        <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
        <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
          <Row>
            <CardList records={sliceVal}/>
          </Row>
          <div style={{display:'flex',justifyContent:'center'}}>
              <Pagination count={pageCount} onChange={handlePageClick} defaultPage={1}	size="large"/>
            </div>

    </div>
  );
}

export default App;
