import React, {Component, useState} from 'react';
import Row from 'react-bootstrap/Row';
import logo_img from './logo.jpg';
import Col from 'react-bootstrap/Col';
import { Container } from 'react-bootstrap';
import Popover from '@material-ui/core/Popover';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Table from 'react-bootstrap/Table'


const useStyles = makeStyles((theme) => ({
    popover: {
      pointerEvents: 'none',
    },
    paper: {
      padding: theme.spacing(1),
    },
  }));



 function UserCards (props) {
     const {firstname,lastname,gender,latitude,longitude,creditcardnumber,
             creditcardtype,email,domainname,phonenumber,macaddress,
             url,username,lastlogin,paymentmethod} = props
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handlePopoverOpen = (event) => {
    setAnchorEl(event.currentTarget);
    };

    const handlePopoverClose = () => {
    setAnchorEl(null);
    };

    const open = Boolean(anchorEl);
            return (                    
                    <Row style={{padding:"25px"}}> 
                        <div>
                            <Typography
                            aria-owns={open ? 'mouse-over-popover' : undefined}
                            aria-haspopup="true"
                            onMouseEnter={handlePopoverOpen}
                            onMouseLeave={handlePopoverClose}
                            >
                                <div className= 'tc dib br3 ba b--light-gray pa3 ma2 bw2 pointer w8' style={{width:"300px"}}>                    
                                    <img alt='robots' src= {`https://robohash.org/segun`} style={{width:"220px",height:"220px"}}/>
                                    <div>
                                        <p style={{paddingTop:'30px', fontFamily: 'Muli', fontSize: '15px', fontWeight:'bold',
                                        textAlignLast:'center', lineHeight: '12px', letterSpacing: '0.2px',color: 'black',}}>
                                            segunorodeji@gmail.com
                                        </p>
                                    </div>
                                    <div style={{paddingTop: '20px', borderTop: '0.5vh solid ', padding:0 , left:0, marginTop: '10px', opacity: 0.06}}></div>
                                    <div>
                                        <p style={{paddingTop:'30px', fontFamily: 'Muli', fontSize: '20px', fontWeight:'bold',
                                        textAlignLast:'center', lineHeight: '12px', letterSpacing: '0.2px',color: 'black',}}>
                                            Segun Orodeji
                                        </p>
                                    </div>
                                </div>
                            </Typography>
                            <Popover
                            id="mouse-over-popover"
                            className={classes.popover}
                            classes={{
                                paper: classes.paper,
                            }}
                            open={open}
                            anchorEl={anchorEl}
                            anchorOrigin={{
                                vertical: 'center',
                                horizontal: 'center',
                            }}
                            transformOrigin={{
                                vertical: 'top',
                                horizontal: 'left',
                            }}
                            onClose={handlePopoverClose}
                            disableRestoreFocus
                            >
                                <Table striped hover style={{width:"700px"}}>
                                    <tbody>
                                        <tr>
                                        <td>First Name</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>Last Name</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>Gender</td>
                                        <td>Otto</td>
                                        </tr>                                       
                                        <tr>
                                        <td>Latitude</td>
                                        <td>Otto</td>
                                        </tr>                                       
                                        <tr>
                                        <td>Longitude</td>
                                        <td>Otto</td>
                                        </tr>                                       
                                        <tr>
                                        <td>Credit Card Number</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>Credit Card Type</td>
                                        <td>Otto</td>
                                        </tr>                                       
                                        <tr>
                                        <td>Email</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>Domain Name</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>Phone Number</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>Mac Address</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>URL</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>Username</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>Last Login</td>
                                        <td>Otto</td>
                                        </tr>
                                        <tr>
                                        <td>Payment Method</td>
                                        <td>Otto</td>
                                        </tr>
                                    </tbody>
                                    </Table>
                            </Popover>
                        </div>




                    </Row>

            )
    }

export default UserCards;
